sysauto_script.script:
// Get today's time and date
	var rightNow = new GlideDateTime();
	// Query the database for NeedIt Task records with Due date field values older than the current time. Only return NeedIt Task records that do not have a State field value of Closed Complete.
	var twentyFourHrs = new GlideRecord('x_58872_needit_needit_task');
	twentyFourHrs.addQuery('due_date', '>=', rightNow);
	twentyFourHrs.addQuery('due_date', '<', gs.hoursAgo(-24));
	twentyFourHrs.addQuery('state', '!=', 3);

	twentyFourHrs.query();
	// Write a log message for each NeedIt Task Record due within 24 hours from now
	while(twentyFourHrs.next()){
		gs.info("Tasks newer than 24 Hours = " + twentyFourHrs.number);
		gs.eventQueue('x_58872_needit.twentyFourHourTasks', twentyFourHrs, twentyFourHrs.number, gs.getUserName());
	}
